mapper.select:
SELECT cno, nickname, ctitle, cregDate, cviewCnt, ccontent
FROM dutch.tbl_notice
WHERE cno = #{cno }

SELECT cno, nickname, ctitle, cregDate, cviewCnt, ccontent
FROM dutch.tbl_notice
WHERE cno = #{cno }

mapper.update:
UPDATE dutch.tbl_notice
SET ctitle = #{ctitle} ,
    ccontent = #{ccontent} 
WHERE cno = #{cno}

UPDATE dutch.tbl_notice
SET cdelFlag = 1
WHERE cno = #{cno}

UPDATE dutch.tbl_notice
SET cviewCnt = cviewCnt + 1
WHERE cno = #{cno}
