mapper.select:
SELECT qno, nickname, qtitle, qcontent, qregDate, qmodDate, qdelFlag, qdelFlagAdmin, qsignal, qreplyCnt
FROM dutch.tbl_qna
WHERE qno = #{qno }

mapper.update:
UPDATE dutch.tbl_qna
SET qdelFlagAdmin =1
WHERE qno = #{qno }

UPDATE dutch.tbl_qna
SET qsignal = 0
WHERE qno = #{qno}

UPDATE dutch.tbl_qna
SET qsignal = 1
WHERE qno = #{qno}

UPDATE dutch.tbl_qna
SET qsignal = 2
WHERE qno = #{qno}

UPDATE dutch.tbl_qna
SET qreplyCnt = qreplyCnt + #{amount}
WHERE qno = #{qno}
